mapper.select:
select posts.id, posts.title, posts.datetime, posts.category, posts.views, posts.comment, posts.highlight, users.id user_id, users.username, users.avatar from posts inner join users on users.phone = posts.phone and posts.status = 1
and category = #{category}
and title REGEXP #{keywords}
order by ${sort} desc

select posts.id, posts.title, posts.datetime, posts.category, posts.views, posts.status, posts.report, posts.comment, users.id user_id, users.username from posts inner join users on users.phone = posts.phone
and category = #{category}
and status = #{status}
and title REGEXP #{keywords}
order by ${sort} desc

mapper.update:
update posts
set views = views + 1
where id = #{id}

update posts
set comment = comment + ${num}
where id = #{id}

update posts
set status = ${status}
where id = #{id}
