mapper.update:
update users
set role = 0
where phone = #{phone}

update users
set role = 1
where phone = #{phone}

mapper.select:
select count(id)
from posts
where category = #{id}

mapper.update:
update category
set category_name = #{category_name}
where id = #{id}

mapper.select:
select id, role, phone, username, gender, sign, avatar, experience, status, datetime, lastTime, (select level_name from level where users.experience >= experience order by experience desc limit 1) as level_name from users where role = 1
and status = #{status}
and users.username REGEXP #{keywords}
or users.phone REGEXP #{keywords}
order by ${sort} desc

mapper.update:
